import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import useMessages from "../../hooks/useMessages";
import Spinner from "../../components/Spinner";

const PatientMessages = () => {
  const { patientId } = useParams();
  const { messages, loading } = useMessages(patientId); // Assuming useMessages fetches messages from the backend
  const [patientMessages, setPatientMessages] = useState([]);


  const mockMessages = [
    {
      id: 1,
      content: 'Hello!',
      dateTime: '2023-11-05T17:31:52.384',
      sender: {
        id: 1,
        email: 'employee@example.com',
        firstName: 'John',
        lastName: 'Doe',
        role: 'EMPLOYEE',
      },
      status: 'ACTIVE',
    },
    {
      id: 2,
      content: 'How are you feeling today?',
      dateTime: '2023-11-06T09:15:00.000',
      sender: {
        id: 1,
        email: 'employee@example.com',
        firstName: 'John',
        lastName: 'Doe',
        role: 'EMPLOYEE',
      },
      status: 'ACTIVE',
    },
  ];
    
  useEffect(() => {
    // Fetch messages for the specific patient using 'patientId'
    // Assuming you have a function to fetch messages, replace 'fetchPatientMessages' with the appropriate function
    /*fetchPatientMessages(patientId)
      .then((data) => {
        setMessages(data);
      })
      .catch((error) => {
        console.error('Error fetching messages:', error);
        // Handle error, show a message, etc.
      });*/
    setPatientMessages(mockMessages);
  }, [patientId]);

  return (
    <div>
      <h1>Messages for Patient ID: {patientId}</h1>
      <Link to="/allpatients" className="btn btn-danger my-2">
        Back to All Patients
      </Link>
      <hr className="my-4" />
      {loading ? (
        <Spinner splash="Loading Messages..." />
      ) : (
        <div>
          {patientMessages.length === 0 ? (
            <h3>No messages for this patient</h3>
          ) : (
            <div>
              <table className="table table-hover">
                <thead>
                  <tr className="table-dark">
                    <th scope="col">Message ID</th>
                    <th scope="col">Content</th>
                    <th scope="col">Date & Time</th>
                    <th scope="col">Replies</th>
                  </tr>
                </thead>
                <tbody>
                  {patientMessages.map((message) => (
                    <tr key={message.id}>
                      <td>{message.id}</td>
                      <td>{message.content}</td>
                      <td>{new Date(message.dateTime).toLocaleString()}</td>
                      <td>
                        <Link to={`/messages/${message.id}`} className="btn btn-info">
                          Replies
                        </Link>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default PatientMessages;
