import { useState, useEffect } from 'react';
import { fetchMessageWithReplies } from '../apis/MessageApi';
import { toast } from 'react-toastify';
import useAuth from './useAuth';

const useMessageWithReplies = (messageId) => {
  const [message, setMessage] = useState();
  const [loading, setLoading] = useState(true);

  const {user} = useAuth()

  useEffect(() => {
    const fetchData = async () => {
      try {

        if (user && user.token) {
          const fetchedMessage = await fetchMessageWithReplies(messageId, user.token);

          if (fetchedMessage) {
            setMessage(fetchedMessage);
          } else {
            // Display an error toast if there was an issue fetching the message
            toast.error('Error fetching message with replies');
          }
        }
      } catch (apiError) {
        // Display an error toast if there was an issue with the API call
        toast.error('Error fetching message with replies');
      } finally {
        // Set loading to false once the data has been fetched or an error occurred
        setLoading(false);
      }
    };

    // Call the fetchData function when the component mounts or when messageId changes
    fetchData();
  }, [messageId]);

  // Return the fetched message and loading state
  return { message, loading };
};

export default useMessageWithReplies;
