import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const MessageDetails = () => {
  const { messageId } = useParams(); // Assuming you get the messageId from the URL
  const [messageWithReplies, setMessageWithReplies] = useState([]);

  // Fetch message with replies
  const mockMessageWithReplies = [
    {
      id: 1,
      content: 'hej',
      dateTime: '2023-11-05T17:31:52.384',
      sender: {
        id: 1,
        email: 'yaman',
        firstName: 'Yaman',
        lastName: 'A',
        role: 'PATIENT',
      },
      status: 'ACTIVE',
      replies: [
        {
          id: 6,
          content: 'reply1',
          dateTime: '2023-11-10T22:59:01.052195',
          sender: {
            id: 1,
            email: 'yaman',
            firstName: 'Yaman',
            lastName: 'A',
            role: 'PATIENT',
          },
        },
        {
          id: 7,
          content: 'reply2',
          dateTime: '2023-11-10T23:01:29.942987',
          sender: {
            id: 2,
            email: 'noor',
            firstName: 'Noor',
            lastName: 'A',
            role: 'EMPLOYEE',
          },
        },
      ],
    },
  ];

  useEffect(() => {
    // Fetch messageWithReplies data for the specific message using 'messageId'
    // For this example, setting the mock messageWithReplies for the specified message
    setMessageWithReplies(mockMessageWithReplies);
  }, [messageId]);

  const renderMessage = (message) => (
    <div key={message.id}>
      <p>
        {message.sender.role === 'PATIENT'
          ? 'Sent by Patient:'
          : `Reply by Employee: ${message.sender.firstName} (${message.sender.email})`}
      </p>
      <p>{message.content}</p>
      <p>Sent at: {message.dateTime}</p>
      {message.replies && message.replies.length > 0 && (
        <div style={{ marginLeft: '20px' }}>
          {message.replies.map((reply) => renderMessage(reply))}
        </div>
      )}
    </div>
  );

  return (
    <div>
      <h1>Message details</h1>
      <div>
        {messageWithReplies.map((message) => renderMessage(message))}
        {/* Add a reply field for the logged-in user (Employee) */}
        <div>
          <input type="text" placeholder="Type your reply" />
          <button>Reply</button>
        </div>
      </div>
    </div>
  );
};

export default MessageDetails;
