import { useState, useEffect } from 'react';
import { fetchMessageWithReplies } from '../apis/MessageApi';
import { toast } from 'react-toastify';
import useAuth from './useAuth';

const useMessageWithReplies = (messageId) => {
  const { get, loading, error } = useApi();
  const [message, setMessage] = useState();

  const {user} = useAuth()

  useEffect(() => {
    const fetchData = async () => {
      try {

        if (user && user.token) {
          const fetchedMessage = await fetchMessageWithReplies(messageId, user.token);

          if (fetchedMessage) {
            setMessage(fetchedMessage);
          } else {
            toast.error('Error fetching message with replies');
          }
        }
      } catch (apiError) {
        toast.error('Error fetching message with replies');
      } 
    };

    // Call the fetchData function when the component mounts or when messageId changes
    fetchData();
  }, [messageId]);

  // Return the fetched message and loading state
  return { message, loading };
};

export default useMessageWithReplies;
