import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Spinner from '../../components/Spinner';
import usePatientNotes from "../../hooks/usePatientNotes";

const NoteDetails = () => {
    const { patientId } = useParams();

    const { patientNotes, loading } = usePatientNotes(patientId);
    const [notes, setNotes] = useState([]);

    const mockNotes = [
        {
            id: 1,
            text: 'Patient has fever. Prescribed antibiotics.',
            dateTimeCreated: '2023-11-05T17:31:52.384',
            employee: {
                id: 1,
                email: 'john@example.com',
                firstName: 'John',
                lastName: 'Doe',
                role: 'DOCTOR',
            },
            patient: {
                id: 1,
                name: 'Jane Doe',
                username: 'jane@example.com',
                age: 25,
                diagnosis: 'Fever',
            },
            encounter: 1,
        },
        {
            id: 2,
            text: 'Patient reports improvement. Continue with prescribed medication.',
            dateTimeCreated: '2023-11-06T09:15:00.000',
            employee: {
                id: 1,
                email: 'john@example.com',
                firstName: 'John',
                lastName: 'Doe',
                role: 'DOCTOR',
            },
            patient: {
                id: 1,
                name: 'Jane Doe',
                username: 'jane@example.com',
                age: 25,
                diagnosis: 'Fever',
            },
            encounter: 2,
        },
        {
            id: 3,
            text: 'Patient presents with cough and sore throat. Prescribed cough syrup and rest.',
            dateTimeCreated: '2023-11-08T14:45:00.000',
            employee: {
                id: 2,
                email: 'emily@example.com',
                firstName: 'Emily',
                lastName: 'Smith',
                role: 'DOCTOR',
            },
            patient: {
                id: 2,
                name: 'John Smith',
                username: 'john@example.com',
                age: 32,
                diagnosis: 'Respiratory Infection',
            },
            encounter: 3,
        },
    ];

    useEffect(() => {
        setNotes(mockNotes)
    }, [patientId]);

    const renderNotes = (note) => (
        <div key={note.id} className='ml-3 bg-light p-3 rounded'>
            <p>
                Written by: {note.employee.firstName} {note.employee.lastName} ({note.employee.email})
            </p>
            <p>{note.text}</p>
            <p>Created at: {new Date(note.dateTimeCreated).toLocaleString()}</p>
            <p>
                Patient: {note.patient.name} ({note.patient.username}), Age: {note.patient.age}, Diagnosis: {note.patient.diagnosis}
            </p>
            {note.encounter && (
                <p>
                    Encounter: {note.encounter.toLocaleString()}
                </p>
            )}
        </div>
    );

    return (
        <div>
            <h1>Patients Notes</h1>
            {loading ? (
                <Spinner splash="Loading Note Details..." />
            ) : (
                <div>
                    {notes.map((note) => renderNotes(note))}
                </div>
            )}
        </div>
    );
};

export default NoteDetails;
