const AllPatients = () => {
  const { toast } = useContext(ToastContext);
  const { patients, loading } = usePatients();

  const [searchInput, setSearchInput] = useState("");
  const [pagePatients, setPagePatients] = useState(patients);

  useEffect(() => {
    setPagePatients(patients); // Update the displayed patients when 'patients' change
  }, [patients]);

  const handleSearchSubmit = (event) => {
    event.preventDefault();

    const newSearchUser = patients.filter((patient) =>
      patient.firstName.toLowerCase().includes(searchInput.toLowerCase()) ||
      patient.lastName.toLowerCase().includes(searchInput.toLowerCase())
    );
    setPagePatients(newSearchUser);
  };

  return (
    <>
      <div>
        <h1>All Patients</h1>
        <a href="/allpatients" className="btn btn-danger my-2">
          Reload Patient List
        </a>
        <hr className="my-4" />
        {loading ? (
          <Spinner splash="Loading Patients..." />
        ) : (
          <>
            {pagePatients.length === 0 ? ( // Use 'pagePatients' for display
              <h3>No patients found</h3>
            ) : (
              <>
                <form className="d-flex" onSubmit={handleSearchSubmit}>
                  <input
                    type="text"
                    name="searchInput"
                    id="searchInput"
                    className="form-control my-2"
                    placeholder="Search Patient"
                    value={searchInput}
                    onChange={(e) => setSearchInput(e.target.value)}
                  />
                  <button type="submit" className="btn btn-info mx-2">
                    Search
                  </button>
                </form>

                <p>
                  Total Patients: <strong>{pagePatients.length}</strong>
                </p>
                <table className="table table-hover">
                  <thead>
                    <tr className="table-dark">
                      <th scope="col">Name</th>
                      <th scope="col">Email</th>
                      <th scope="col">Profile</th>
                    </tr>
                  </thead>
                  <tbody>
                    {pagePatients.map((patient) => ( // Display 'pagePatients' instead of 'patients'
                      <tr key={patient._id}>
                        <th scope="row">{patient.firstName + " " + patient.lastName}</th>
                        <td>{patient.email}</td>
                        <td>
                          <Link to={`/patient/${patient.id}`} className="btn btn-info my-2">
                            Profile
                          </Link>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </>
            )}
          </>
        )}
      </div>
    </>
  );
};

export default AllPatients;
