import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import AuthContext from '../../context/AuthContext';

const Register = () => {
  const { registerPatient } = useContext(AuthContext);

  const [credentials, setCredentials] = React.useState({
    email: '',
    password: '',
    confirmPassword: '',
    firstName: '',
    lastName: '',
    birthdate: '', // New field for birth date
  });

  const handleChange = (e) => {
    setCredentials({
      ...credentials,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (
      !credentials.email ||
      !credentials.password ||
      !credentials.confirmPassword ||
      !credentials.firstName ||
      !credentials.lastName ||
      !credentials.birthdate // Check for the birthdate field
    ) {
      toast.error('Please fill in all fields');
      return;
    }

    if (credentials.confirmPassword !== credentials.password) {
      toast.error('The passwords do not match');
      return;
    }

    const userData = { ...credentials, confirmPassword: undefined };
    registerPatient(userData);
  };

  return (
    <>
      <ToastContainer autoClose={2000} />
      <form onSubmit={handleSubmit}>
        <h3>Create a patient account</h3>
        {/* Other form fields */}
        <div className="form-group">
          <label htmlFor="birthdate" className="form-label mt-4">
            Birth Date
          </label>
          <input
            type="date"
            className="form-control"
            id="birthdate"
            name="birthdate"
            value={credentials.birthdate}
            onChange={handleChange}
            required
          />
        </div>
        <input type="submit" value="Register" className="btn btn-primary my-3" />
        <p>
          Already have an account? <Link to="/login">Log in</Link>
        </p>
      </form>
    </>
  );
};

export default Register;
