import { useParams } from "react-router-dom";
import Spinner from "../../components/Spinner";
import { useEffect, useState } from "react";
import useAuth from '../../hooks/useAuth';
import useMessageWithReplies from "../../hooks/useMessageWithReplies";
import { toast } from "react-toastify";
import useApi from "../../hooks/useApi";

const MyMessageDetails = () => {
  const { messageId } = useParams(); 
  const { get, loading, error } = useApi();
  const {user} = useAuth();
  
  const [messageDetails, setMessageDetails] = useState();

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (user && user.token) {
          const fetchedMessage = await get(`/messages/${messageId}`, user.token);
          console.log("id "+ fetchedMessage.id);
          if (fetchedMessage) {
            setMessageDetails(fetchedMessage);
          } else {
            toast.error('Error fetching message with replies');
          }
        }
      } catch (apiError) {
        toast.error('Error fetching message with replies');
      } 
    };

    fetchData();
    console.log(messageDetails)
  }, []);

  const renderMessage = (msg) => (
    <div key={msg.id} className='ml-3 bg-light p-3 rounded'>
      {msg.sender && (
        <p>
          Patient: {msg.sender.firstName} {msg.sender.lastName} ({msg.sender.email})
        </p>
      )}
      <p>{msg.content}</p>
      <p>Sent at: {msg.dateTime && new Date(msg.dateTime).toLocaleString()}</p>
      {msg.replies && msg.replies.length > 0 && (
        <div>
          {msg.replies.map((reply, index) => (
            <div key={reply.id} className="border rounded p-2 my-2">
              <p>{reply.content}</p>
              <p>Sent by: {reply.sender && `${reply.sender.firstName} ${reply.sender.lastName} (${reply.sender.email})`} 
                  , at {reply.dateTime && new Date(reply.dateTime).toLocaleString()}</p>  
            </div>
          ))}
        </div>
      )}
    </div>
  );

  return (
    <div>
      <h1>Message details</h1>
      {loading ? (
        <Spinner splash="Loading Message Details..." />
      ) : (
        <div>
          { renderMessage(messageDetails)}
          <div className="mt-3">
            <input type="text" placeholder="Type your reply" className="form-control" />
            <button className="btn btn-primary my-3">Reply</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default MyMessageDetails;
