import React from 'react';
import { Link } from 'react-router-dom';

import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Register = () => {
  const [credentials, setCredentials] = React.useState({
    email: '',
    password: '',
    confirmPassword: '',
    firstName: '',
    lastName: ''
  });

  const handleChange = (e) => {
    setCredentials({
      ...credentials,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if(!credntials.email ||!credntials.password ||!credntials.confirmPassword ||!credntials.firstName ||!credntials.lastName) {
      toast.error('Please fill in all fields')
      return;
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Create a patient account</h3>
      <div className="form-group">
        <label htmlFor="firstName" className="form-label mt-4">First Name</label>
        <input
          type="text"
          className="form-control"
          id="firstName"
          name="firstName"
          value={credentials.firstName}
          onChange={handleChange}
          placeholder="Enter first name"
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="lastName" className="form-label mt-4">Last Name</label>
        <input
          type="text"
          className="form-control"
          id="lastName"
          name="lastName"
          value={credentials.lastName}
          onChange={handleChange}
          placeholder="Enter last name"
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="emailInput" className="form-label mt-4">Email address</label>
        <input
          type="email"
          className="form-control"
          id="emailInput"
          name="email"
          value={credentials.email}
          onChange={handleChange}
          placeholder="Enter email"
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="passwordInput" className="form-label mt-4">Password</label>
        <input
          type="password"
          className="form-control"
          id="passwordInput"
          name="password"
          value={credentials.password}
          onChange={handleChange}
          placeholder="Enter password"
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="confirmPassword" className="form-label mt-4">Confirm password</label>
        <input
          type="password"
          className="form-control"
          id="confirmPassword"
          name="confirmPassword"
          value={credentials.confirmPassword}
          onChange={handleChange}
          placeholder="Confirm password"
          required
        />
      </div>
      <input type="submit" value="Register" className="btn btn-primary my-3" />
      <p>Already have an account? <Link to="/login">Log in</Link></p>
    </form>
  );
};

export default Register;
