import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const MessageReplies = () => {
  const { messageId } = useParams(); // Assuming you get the messageId from the URL
  const [MessageWithReplies, setMessageWithReplies] = useState([]);

  // Fetch message with replies
  const mockMessageWithReplies = [
    {
      id: 1,
      content: 'Hello!',
      dateTime: '2023-11-05T17:31:52.384',
      sender: {
        id: 1,
        email: 'patient@example.com',
        firstName: 'John',
        lastName: 'Doe',
        role: 'PATIENT',
      },
      replies: [
        {
          id: 2,
          content: 'Hi there!',
          dateTime: '2023-11-05T17:35:00.000',
          sender: {
            id: 1,
            email: 'employee@example.com',
            firstName: 'Emily',
            lastName: 'Smith',
            role: 'EMPLOYEE',
          },
        },
        // More replies can be added
      ],
    },
  ];

  useEffect(() => {
    // Fetch conversation data for the specific message using 'messageId'
    // For this example, setting the mock conversation for the specified message
    setConversation(mockConversation);
  }, [messageId]);

  return (
    <div>
      <h1>Message</h1>
      <div>
        {conversation.map((message) => (
          <div key={message.id}>
            <p>
              {message.sender.role === 'PATIENT'
                ? 'Sent by Patient:'
                : `Reply by Employee: ${message.sender.firstName} (${message.sender.email})`}
            </p>
            <p>{message.content}</p>
            <p>Sent at: {message.dateTime}</p>
          </div>
        ))}
        {/* Add a reply field for the logged-in user (Employee) */}
        <div>
          <input type="text" placeholder="Type your reply" />
          <button>Reply</button>
        </div>
      </div>
    </div>
  );
};

export default MessageReplies;
