import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Spinner from '../../components/Spinner';
import usePatientNotes from "../../hooks/usePatientNotes";

const NoteDetails = () => {
    const { patientId } = useParams();

    const { patientNotes, loading } = usePatientNotes(patientId);
    const [note, setNotes] = useState([]);

    const mockNotes = [
        {
            id: 1,
            text: 'Patient has fever. Prescribed antibiotics.',
            dateTimeCreated: '2023-11-05T17:31:52.384',
            employee: {
                id: 1,
                email: 'john@example.com',
                firstName: 'John',
                lastName: 'Doe',
                role: 'DOCTOR',
            },
            patient: {
                id: 1,
                name: 'Jane Doe',
                username: 'jane@example.com',
                age: 25,
                diagnosis: 'Fever',
            },
            encounter: {
                id: 1,
                type: 'Follow-up',
                dateTime: '2023-11-05T18:00:00.000',
            },
        },
        {
            id: 2,
            text: 'Patient reports improvement. Continue with prescribed medication.',
            dateTimeCreated: '2023-11-06T09:15:00.000',
            employee: {
                id: 1,
                email: 'john@example.com',
                firstName: 'John',
                lastName: 'Doe',
                role: 'DOCTOR',
            },
            patient: {
                id: 1,
                name: 'Jane Doe',
                username: 'jane@example.com',
                age: 25,
                diagnosis: 'Fever',
            },
            encounter: {
                id: 1,
                type: 'Follow-up',
                dateTime: '2023-11-07T10:00:00.000',
            },
        },
        {
            id: 3,
            text: 'Patient presents with cough and sore throat. Prescribed cough syrup and rest.',
            dateTimeCreated: '2023-11-08T14:45:00.000',
            employee: {
                id: 2,
                email: 'emily@example.com',
                firstName: 'Emily',
                lastName: 'Smith',
                role: 'DOCTOR',
            },
            patient: {
                id: 2,
                name: 'John Smith',
                username: 'john@example.com',
                age: 32,
                diagnosis: 'Respiratory Infection',
            },
            encounter: {
                id: 2,
                type: 'Initial Consultation',
                dateTime: '2023-11-08T15:00:00.000',
            },
        },
    ];

    useEffect(() => {
        setNotes(mockNotes)
    }, [patientId]);

    const renderNotes = (note) => (
        <div key={note.id} className='ml-3 bg-light p-3 rounded'>
            <p>
                Doctor: {note.employee.firstName} {note.employee.lastName} ({note.employee.email})
            </p>
            <p>{note.text}</p>
            <p>Created at: {new Date(note.dateTimeCreated).toLocaleString()}</p>
            <p>
                Patient: {note.patient.name} ({note.patient.username}), Age: {note.patient.age}, Diagnosis: {note.patient.diagnosis}
            </p>
            {note.encounter && (
                <p>
                    Encounter: {note.encounter.type} at {new Date(note.encounter.dateTime).toLocaleString()}
                </p>
            )}
        </div>
    );

    return (
        <div>
            <h1>Note details</h1>
            {loading ? (
                <Spinner splash="Loading Note Details..." />
            ) : (
                <div>
                    {note ? renderNotes(note) : <p>Note not found</p>}
                </div>
            )}
        </div>
    );
};

export default NoteDetails;
