import React, { useContext, useEffect, useState } from "react";
import { Modal } from "react-bootstrap";
import { Link } from "react-router-dom";
import Spinner from "../components/Spinner";
import ToastContext from "../context/ToastContext";
import { getAllPatients, deletePatient } from "../api/PatientApi";
import usePatients from "../hooks/usePatients";

const AllPatients = () => {
  const { toast } = useContext(ToastContext);
  const { patients } = usePatients();

  const handleSearchSubmit = (event) => {
    event.preventDefault();

    const newSearchUser = patients.filter((patient) =>
      patient.firstName.toLowerCase().includes(searchInput.toLowerCase())
    );
    setPatients(newSearchUser);
  };

  return (
    <>
      <div>
        <h1>Your Patients</h1>
        <a href="/mypatients" className="btn btn-danger my-2">
          Reload Patient
        </a>
        <hr className="my-4" />
        {loading ? (
          <Spinner splash="Loading Patients..." />
        ) : (
          <>
            {patients.length == 0 ? (
              <h3>No patients found</h3>
            ) : (
              <>
                <form className="d-flex" onSubmit={handleSearchSubmit}>
                  <input
                    type="text"
                    name="searchInput"
                    id="searchInput"
                    className="form-control my-2"
                    placeholder="Search Patient"
                    value={searchInput}
                    onChange={(e) => setSearchInput(e.target.value)}
                  />
                  <button type="submit" className="btn btn-info mx-2">
                    Search
                  </button>
                </form>

                <p>
                  Your Total Patients: <strong>{patients.length}</strong>
                </p>
                <table className="table table-hover">
                  <thead>
                    <tr className="table-dark">
                      <th scope="col">Name</th>
                      <th scope="col">Address</th>
                      <th scope="col">Email</th>
                      <th scope="col">Phone</th>
                    </tr>
                  </thead>
                  <tbody>
                    {patients.map((patient) => (
                      <tr
                        key={patient._id}
                        onClick={() => {
                          setModalData({});
                          setModalData(patient);
                          setShowModal(true);
                        }}
                      >
                        <th scope="row">{patient.firstName}</th>
                        <td>{patient.address}</td>
                        <td>{patient.email}</td>
                        <td>{patient.phone}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </>
            )}
          </>
        )}
      </div>

      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{modalData.firstName}</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <h3>{modalData.firstName}</h3>
          <p>
            <strong>Address</strong>: {modalData.address}
          </p>
          <p>
            <strong>Email</strong>: {modalData.email}
          </p>
          <p>
            <strong>Phone Number</strong>: {modalData.phone}
          </p>
        </Modal.Body>

        <Modal.Footer>
          <Link className="btn btn-info" to={`/edit/${modalData._id}`}>
            Edit
          </Link>
          <button
            className="btn btn-danger"
            onClick={() => deletePatient(modalData._id)}
          >
            Delete
          </button>
          <button
            className="btn btn-warning"
            onClick={() => setShowModal(false)}
          >
            Close
          </button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default AllPatients;
