import { useParams } from "react-router-dom";
import Spinner from "../../components/Spinner";
import { useEffect, useState } from "react";
import useAuth from '../../hooks/useAuth';
import useMessageWithReplies from "../../hooks/useMessageWithReplies";
import { toast } from "react-toastify";
import useApi from "../../hooks/useApi";

const MyMessageDetails = () => {
  const { messageId } = useParams(); 
  const {message, loading} = useMessageWithReplies(messageId);
  const {user} = useAuth();
  
  const [messageDetails, setMessageDetails] = useState();

  useEffect(() => {
    setMessageDetails(message); 
  }, [messageId, message]); 



  const renderReplies = (reply) => (
    <div key={reply.id} className='ml-3 bg-light p-3 rounded mb-3'>
      <p>
        Written by: {reply.sender.firstName} {reply.sender.lastName} ({reply.sender.email})
      </p>
      <p>{reply.content}</p>
      <p>Created at: {new Date(reply.dateTime).toLocaleString()}</p>
    </div>
  );
  

  return (
    <div>
      <h1>Message Details</h1>
      {loading ? (
        <Spinner splash="Loading Message Details..." />
      ) : (
        <div>
          {messageDetails && messageDetails.replies && messageDetails.replies.map((message) => renderReplies(message))}
          <div className="mt-3">
            <input type="text" placeholder="Type your reply" className="form-control" />
            <button className="btn btn-primary my-3">Reply</button>
          </div>
        </div>
      )}
    </div>
  );
};
export default MyMessageDetails;
