import React from 'react'
import { Link } from 'react-router-dom'

import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Login = () => {

  const [credntials, setCredentials] = React.useState({
    email: '',
    password: ''
  })

  const handleChange = (e) => {
    setCredentials({
    ...credntials,
      [e.target.name]: e.target.value
    })
  }

  const handleSubmit = (e) => {
    e.preventDefault()

    if(!credntials.email ||!credntials.password) {
      alert('Please fill in all fields')
      return
    }
  }
 
  return (
    <>
      <form onSubmit={handleSubmit}>
          <h3>Login</h3>
          <div class="form-group">
              <label for="emailInput" class="form-label mt-4">Email address</label>
              <input 
                type="email" 
                class="form-control" 
                id="emailInput"  
                name="email"
                value={credntials.email}
                onChange={handleChange}
                placeholder="Enter email" 
                required
              />
          </div>
          <div class="form-group">
              <label for="passwordInput" class="form-label mt-4">Password</label>
              <input 
                type="password" 
                class="form-control" 
                id="passwordInput" 
                name="password"
                value={credntials.password}
                onChange={handleChange}
                placeholder="Enter password"  
                required
              />
          </div>
          <input type="submit" value="Login" className="btn btn-primary my-3"/>
          <p>Don't have an account? <Link to="/register">Create one</Link></p>
      </form>
    </>
  )
}

export default Login;
