import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Spinner from "../../components/Spinner";
import useAllPatientMessages from "../../hooks/useAllPatientMessages"; // Assuming a new hook for all patient messages

const ActiveMessages = () => {
  const { allPatientMessages, loading } = useAllPatientMessages(); // Update hook
  const [messages, setMessages] = useState([]);

  const mockMessages = [
    // ... (existing mock messages)
  ];

  useEffect(() => {
    setMessages(mockMessages); // Replace with actual data fetch logic
  }, []);

  return (
    <div>
      <h1>All Active Messages</h1>
      <Link to="/allpatients" className="btn btn-danger my-2">
        Back to All Patients
      </Link>
      <Link to="/messages/archived" className="btn btn-secondary my-2 mx-2">
        View Archived Messages
      </Link>
      <hr className="my-4" />
      {loading ? (
        <Spinner splash="Loading Messages..." />
      ) : (
        <div>
          {messages.length === 0 ? (
            <h3>No active messages</h3>
          ) : (
            <div>
              <table className="table table-hover">
                <thead>
                  <tr className="table-dark">
                    <th scope="col">Content</th>
                    <th scope="col">Date & Time</th>
                    <th scope="col">Patient Name</th>
                    <th scope="col">Status</th>
                    <th scope="col">Replies</th>
                    <th scope="col"></th>
                  </tr>
                </thead>
                <tbody>
                  {messages.map((message) => (
                    <tr key={message.id}>
                      <td>{message.content}</td>
                      <td>{new Date(message.dateTime).toLocaleString()}</td>
                      <td>{`${message.sender.firstName} ${message.sender.lastName}`}</td>
                      <td>{message.status}</td>
                      <td>
                        <Link to={`/messages/${message.id}`} className="btn btn-info">
                          Replies
                        </Link>
                      </td>
                      <td>
                        <button
                          className="btn btn-warning"
                          onClick={() => console.log("Archived")}
                        >
                          Archive
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default ActiveMessages;
