import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import useMessageWithReplies from "../../hooks/useMessageWithReplies";
import Spinner from '../../components/Spinner';

const MessageDetails = () => {
  const { messageId } = useParams(); 

  const { message, loading } = useMessageWithReplies(messageId);
  const [messageDetails, setMessageDetails] = useState([]);

  useEffect(() => {
    setMessageDetails(message);
  }, [messageId, message]);

  const renderMessage = (msg) => (
    <div key={msg.id} className='ml-3 bg-light p-3 rounded'>
      <p>
        Patient: {msg.sender.firstName} {msg.sender.lastName} ({msg.sender.email})
      </p>
      <p>{msg.content}</p>
      <p>Sent at: {new Date(msg.dateTime).toLocaleString()}</p>
      {msg.replies && msg.replies.length > 0 && (
        <div>
          {msg.replies.map((reply, index) => (
            <div key={reply.id} className="border rounded p-2 my-2">
              <p>{reply.content}</p>
              <p>
                Sent by: {reply.sender && `${reply.sender.firstName} ${reply.sender.lastName} (${reply.sender.email})`}
                , at {new Date(reply.dateTime).toLocaleString()}
              </p>
            </div>
          ))}
        </div>
      )}
    </div>
  );

  return (
    <div>
      <h1>Message details</h1>
      {loading ? (
        <Spinner splash="Loading Message Details..." />
      ) : (
        <div>
          {renderMessage(messageDetails )}

        </div>
      )}
    </div>
  );
};

export default MessageDetails;
