import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const PatientMessages = () => {
  const { patientId } = useParams();
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    // Fetch messages for the specific patient using 'patientId'
    // Assuming you have a function to fetch messages, replace 'fetchPatientMessages' with the appropriate function
    /*fetchPatientMessages(patientId)
      .then((data) => {
        setMessages(data);
      })
      .catch((error) => {
        console.error('Error fetching messages:', error);
        // Handle error, show a message, etc.
      });*/
  }, [patientId]);

  return (
    <div>
      <h1>Messages for Patient {patientId}</h1>
      <div>
        {messages.length === 0 ? (
          <p>No messages found for this patient.</p>
        ) : (
          <ul>
            {messages.map((message) => (
              <li key={message.id}>
                <p>{message.content}</p>
                <p>Sent by: {message.sender.firstName} {message.sender.lastName}</p>
                <p>Sent at: {message.dateTime}</p>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default PatientMessages;
