import { useEffect, useState } from 'react';
import useApi from './useApi';
import useAuth from './useAuth';
import { toast } from 'react-toastify';

const useNotes = (patientId) => {
  const { get, loading, error } = useApi();
  const [messages, setMessages] = useState([]);

  const {user} = useAuth()

  useEffect(() => {
    const fetchData = async () => {
        try {
            // Assuming fetchPatientNotes is a function in your API module
            const user = JSON.parse(localStorage.getItem('user'));

            if (user !== null && user.token !== null) {
                const fetchedNotes = await get(`/patient/${patientId}/notes`, user.token);

                if (fetchedNotes) {
                    setMessages(fetchedNotes);
                } else {
                // Handle error if needed
                    toast.error('Error fetching patient notes');
                }
            }
        } catch (apiError) {
        toast.error('Error fetching patient notes');
        console.error('API Error:', apiError);
        }
    };

    fetchData();
  }, [patientId]);

  return { messages, loading, error };
};

export default useNotes;
